import { ChangeDetectionStrategy, Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './app.component.html',
  styleUrl: './app.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class AppComponent {
  title = 'design-systems';
}

import {
  ChangeDetectionStrategy,
  Component,
  HostBinding,
  Input,
} from '@angular/core';
import {
  FormInputComponent,
  InputType,
} from '../form-input/form-input.component';

@Component({
  selector: 'app-form-element',
  standalone: true,
  imports: [FormInputComponent],
  templateUrl: './form-element.component.html',
  styleUrl: './form-element.component.css',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class FormElementComponent {
  /**
   * label of the input
   *
   * @since 0.0.3
   * @summary label
   * @author Christian Illies<c.illies@finatix.de>
   * @package ui
   */
  @Input({ required: true }) label: string = '';
  /**
   * Change the type of the input
   *
   * @since 0.0.1
   * @summary Type of the input
   * @author Christian Illies<c.illies@finatix.de>
   * @package ui
   */
  @Input() type: InputType = 'text';

  /**
   * Placeholder of the input
   *
   * @since 0.0.3
   * @summary Placeholder when no value is provided
   * @author Christian Illies<c.illies@finatix.de>
   * @package ui
   */
  @Input() placeholder: string = '';
  /**
   * value of the input
   *
   * @since 0.0.3
   * @summary value
   * @author Christian Illies<c.illies@finatix.de>
   * @package ui
   */
  @Input() value: string | undefined;

  protected id: string = crypto.randomUUID();

  @HostBinding('class') get classNames() {
    return this.type;
  }
}

import { ChangeDetectionStrategy, Component, Input } from '@angular/core';
import { IconComponent } from '../../icon/icon.component';

export type InputType = 'text' | 'password' | 'email';

/**
 * A simple input that can have click listeners and so on.
 *
 * Usage:
 *
 * ```html
 * <app-form-input type="text" />
 * ```
 *
 * @since 0.0.1
 * @author Christian Illies<c.illies@finatix.de>
 * @public
 * @package ui
 */
@Component({
  selector: 'app-form-input',
  standalone: true,
  imports: [IconComponent],
  templateUrl: './form-input.component.html',
  styleUrl: './form-input.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class FormInputComponent {
  /**
   * Change the type of the input
   *
   * @since 0.0.1
   * @summary Type of the input
   * @author Christian Illies<c.illies@finatix.de>
   * @package ui
   */
  @Input() type: InputType = 'text';

  /**
   * Placeholder of the input
   *
   * @since 0.0.3
   * @summary Placeholder when no value is provided
   * @author Christian Illies<c.illies@finatix.de>
   * @package ui
   */
  @Input() placeholder: string = '';
  /**
   * value of the input
   *
   * @since 0.0.3
   * @summary value
   * @author Christian Illies<c.illies@finatix.de>
   * @package ui
   */
  @Input() value: string | undefined;
  /**
   * Placeholder of the input
   *
   * @since 0.0.3
   * @summary Placeholder when no value is provided
   * @author Christian Illies<c.illies@finatix.de>
   * @package ui
   * @internal
   */
  @Input() id: string = crypto.randomUUID();
}

import { Component, HostBinding, Input } from '@angular/core';

@Component({
  selector: 'app-form-layout',
  standalone: true,
  imports: [],
  templateUrl: './form-layout.component.html',
  styleUrl: './form-layout.component.css',
})
export class FormLayoutComponent {
  @Input() columns = 1;

  @HostBinding('style.grid-template-columns')
  get templateColumns() {
    return `repeat(${this.columns}, 1fr);`;
  }
}

import {ChangeDetectionStrategy, Component, inject, Input,} from '@angular/core';
import {FaIconLibrary, FontAwesomeModule,} from '@fortawesome/angular-fontawesome';
import {fas} from '@fortawesome/free-solid-svg-icons';
import {far, IconName} from '@fortawesome/free-regular-svg-icons';

/**
 * A simple icon of [Fontawesome Library (free)](https://fontawesome.com/search?o=r&m=free&f=classic)
 *
 * Usage:
 *
 * ```html
 * <app-icon iconName="fingerprint" />
 * ```
 *
 * @since 0.0.2
 * @author Christian Illies<c.illies@finatix.de>
 * @public
 * @package ui
 */
@Component({
  selector: 'app-icon',
  standalone: true,
  imports: [FontAwesomeModule],
  templateUrl: './icon.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class IconComponent {
  /**
   * the icon name in font-awesome you want to use.
   *
   * Samples: fingerprint, coffee, arrow-right, ...
   *
   * [Fontawesome Library (free)](https://fontawesome.com/search?o=r&m=free&f=classic)
   */
  @Input({ required: true }) iconName: IconName = 'fingerprint';

  constructor() {
    inject(FaIconLibrary).addIconPacks(fas, far);
  }
}

